class MovieByGenreModel {
  MovieByGenreModel({
    num? page,
    List<MovieModel>? movies,
    num? totalPages,
    num? totalResults,
  }) {
    _page = page;
    _movies = movies;
    _totalPages = totalPages;
    _totalResults = totalResults;
  }

  MovieByGenreModel.fromJson(dynamic json) {
    _page = json['page'];
    if (json['results'] != null) {
      _movies = [];
      json['results'].forEach((v) {
        _movies?.add(MovieModel.fromJson(v));
      });
    }
    _totalPages = json['total_pages'];
    _totalResults = json['total_results'];
  }
  num? _page;
  List<MovieModel>? _movies;
  num? _totalPages;
  num? _totalResults;
  MovieByGenreModel copyWith({
    num? page,
    List<MovieModel>? movies,
    num? totalPages,
    num? totalResults,
  }) =>
      MovieByGenreModel(
        page: page ?? _page,
        movies: movies ?? _movies,
        totalPages: totalPages ?? _totalPages,
        totalResults: totalResults ?? _totalResults,
      );
  num get page => _page ?? 1;
  List<MovieModel> get movies => _movies ?? [];
  num get totalPages => _totalPages ?? 0;
  num get totalResults => _totalResults ?? 0;

  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    map['page'] = _page;
    if (_movies != null) {
      map['movies'] = _movies?.map((v) => v.toJson()).toList();
    }
    map['total_pages'] = _totalPages;
    map['total_results'] = _totalResults;
    return map;
  }

  MovieByGenre toEntity() {
    return MovieByGenre(
        movies: movies.map((e) => e.toEntity()).toList(),
        page: page.toInt(),
        totalPage: totalPages.toInt());
  }
}
